import { AddCircleOutlined, SubjectOutlined } from "@mui/icons-material";
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Typography, AppBar, Toolbar } from "@mui/material";
import { makeStyles, createStyles } from "@mui/styles";
import React from "react";
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useLocation, useNavigate } from "react-router-dom";

const drawerWidth = 240

const useStyles = makeStyles((theme) =>
  createStyles(
    {
        page: {
            background: "#f9f9f9",
            width: '100%'
        },
        drawer: {
            width: drawerWidth
        },
        drawerPaper: {
            width: drawerWidth
        },
        root: {
            display: "flex"
        },
        active: {
            backgroundColor: "#f9f9f9",
        },
        appbar: {
            width: `100% - ${drawerWidth}px`,
            // height: ''
        },
        // toolbar: theme.minxins.toolbar
    }
  ),
);

const theme = createTheme()
export default function Layout( {children} ){

    const classes = useStyles()
    const nav = useNavigate()
    const location = useLocation()
    const manuItem = [
        {
            text: "login",
            icon: <SubjectOutlined color="secondary"/>,
            path: "/login"
        },
        {
            text: "signup",
            icon: <AddCircleOutlined color="secondary"/>,
            path: "/signup"
        }
    ]

    return (

        <ThemeProvider theme = {theme}>
        <div className={classes.root}>
            {/* app bar */}

            {/* side drawer */}
            <Drawer
            className={classes.drawer}
            variant="permanent"
            anchor="left"
            classes={{paper: classes.drawerPaper}}>

                <div>
                    <Typography variant="h4">4_Celery</Typography>
                </div>
                <List>
                    {/* <ListItem>
                        <ListItemText primary = "headline"/>
                    </ListItem>
                    <ListItem>
                        <ListItemText primary = "headline2"/>
                    </ListItem> */}
                    {manuItem.map(item=>(
                        <ListItem
                        key = {item.text}
                        button
                        onClick={()=>nav(item.path)}
                        className={location.pathname == item.path ? classes.active : null}
                        >
                            <ListItemIcon>{item.icon}</ListItemIcon>
                            <ListItemText primary={item.text}/>
                        </ListItem>

                    ))}
                </List>


            </Drawer>
            <AppBar 
             className={classes.appbar}
             position="fixed" 
             className={classes.appBar}
             elevation={0}
             color="primary"
             >
                <Toolbar>
                    <Typography>Welcome to here</Typography>
                </Toolbar>

            </AppBar>

            <div className={classes.page}>
                {/* <div className={classes.toolbar}></div> */}
                {children}
            </div>
            {/* {children} */}
        </div>
        </ThemeProvider>

    )



}
