//             debtors:[]
//         }
//     }

const handleSubmit = () => {

}
const onChange = (e) => {

}


const CreateBillComponent = () => {

    const [state, setState] = useState({
        totalAmount: 0,
        debtors: [],
        type: '',
        comment: '',
        receipt: null
    })
    navigator = useNavigate()
    // this.setState({[event.target.name]:event.target.value})
    const handleSubmit = (state) => {

        axios.post(`${API_URL}/create_bill`,state,{headers:{'content-type': 'multipart/form-data'}})
            .then()


    }


    return (
        <div>
            <h1>create new bill</h1>
            {/* <form action={`${API_URL}/create_bill`} 
                method = 'post'
                className = "form" 
                encType="multipart/form-data"> */}
            <form onSubmit={() => {handleSubmit}}>
                <fieldset>
                    <label>total amount:</label>
                    <input name = "total" type="number" onChange={(e)=>{setState({[e.target.name]: e.target.value})}}></input>
                </fieldset>

                <fieldset>
                    <label>debtor1 email:</label>
                    <input name = "debtors[0][email]" type="email" placeholder = "debtor1email"
                    ></input>

                    <label>debtor1 amount:</label>
                    <input name = "debtors[0][amount]" type="number" placeholder = "amount"></input>
                </fieldset>

                <fieldset>
                    <label>type:</label>
                    <input name = "type" type="text" placeholder = "type" onChange={(e)=>{setState({[e.target.name]: e.target.value})}}></input>
                </fieldset>

                <fieldset>
                    <label>comment:</label>
                    <input name = "comment" type="text" placeholder = "comment" onChange={(e)=>{setState({[e.target.name]: e.target.value})}}></input>
                </fieldset>


                <fieldset>
                    <label>upload receipt:</label>
                    <input type="file" name="receipt" accept="image/png, image/jpeg, .docx, .pdf"
                    onChange={(e) => {setState({receipt:e.target.files[0]})}}></input>
                </fieldset>
                <button type="submit" className='btn-success' >Submit</button>
            </form>
        </div>
    )
}

export default CreateBillComponent;
