import { AddCircleOutlined, SubjectOutlined } from "@mui/icons-material";
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Typography, AppBar, Toolbar, Container } from "@mui/material";
import { makeStyles, createStyles } from "@mui/styles";
import React from "react";
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useLocation, useNavigate } from "react-router-dom";
import { styled } from '@mui/system';

const drawerWidth = 240

const useStyles = makeStyles((theme) =>
  createStyles(
    {
        page: {
            background: "#f9f9f9",
            width: '100%'
        },
        drawer: {
            width: drawerWidth
        },
        drawerPaper: {
            width: drawerWidth
        },
        root: {
            display: "flex"
        },
        active: {
            backgroundColor: "#f9f9f9",
        },
        appBar: {
            width: `calc(100% - ${drawerWidth}px)`,
            marginLeft: 240,
            padding: 240
            // height: ''
        }

    }
  )
);

const theme = createTheme()

export default function Layout( {children} ){
    const Offset = styled('div')(({ theme }) => theme.mixins.toolbar);
    const classes = useStyles()
    const nav = useNavigate()
    const location = useLocation()
    const manuItem = [
        {
            text: "login",
            icon: <SubjectOutlined color="secondary"/>,
            path: "/login"
        },
        {
            text: "signup",
            icon: <AddCircleOutlined color="secondary"/>,
            path: "/signup"
        }
    ]

    return (

        <ThemeProvider theme = {theme}>
        <div className={classes.root}>
        {/* <Container> */}
        <Drawer
            className={classes.drawer}
            variant="permanent"
            anchor="left"
            classes={{paper: classes.drawerPaper}}>

                <div>
                    <Typography variant="h4">4_Celery</Typography>
                </div>
                <List>

                    {manuItem.map(item=>(
                        <ListItem
                        key = {item.text}
                        button
                        onClick={()=>nav(item.path)}
                        className={location.pathname == item.path ? classes.active : null}
                        >
                            <ListItemIcon>{item.icon}</ListItemIcon>
                            <ListItemText primary={item.text}/>
                        </ListItem>

                    ))}
                </List>

            </Drawer>
            {/* </Container> */}

            {/* app bar */}
            <Container>
            <AppBar 
             className={classes.appBar}
             position="fixed" 
             elevation={0}
             color="primary"
             >
                <Toolbar>
                    <Typography>Welcome to heredsfasdfasdfasdfasd </Typography>
                </Toolbar>
            </AppBar>
            <Offset/>            

            <div className={classes.page}>
                {/* <div className={classes.toolbar}></div> */}
                {children}
            </div>
            </Container>
            {/* {children} */}
        </div>
        </ThemeProvider>

    )



}
